sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   var selectedDateStr = newValue;
   alert(selectedDateStr);

   if (selectedDateStr) {
      var selectedDate = new Date(selectedDateStr);

      // Get today's date
      var today = new Date();
      today.setHours(0, 0, 0, 0); 

      // Get yesterday's date
      var yesterday = new Date();
      yesterday.setDate(today.getDate() - 1);
      yesterday.setHours(0, 0, 0, 0); 
		g_form.addInfoMessage(selectedDate + ' ' + yesterday);
      // Check if the selected date is yesterday's date or earlier
      if (selectedDate <= yesterday) {
         g_form.addErrorMessage('Select a date from today onwards, cannot plan on dates before today.');
         g_form.clearValue('installed'); //dsd
      }
   }

}
